mapper.update:
update sale_order
set

    payState=#{payState}


    , orderState=#{orderState}


    , backNum=#{backNum}

where wechatOrder=#{num} and payState <>2

mapper.select:
SELECT
SUM( price ) AS sale,
COUNT( productId ) AS count,
( SELECT COUNT( 1 ) FROM sale_device  where  tenantId=1
) AS device,
( SELECT COUNT( 1 ) FROM sale_log


        creationTime >= #{start}


        and creationTime <= #{end}


) AS error,
( SELECT COUNT( 1 ) FROM sale_order

    ( payState =2 OR orderState =- 1 ) and tenantId=1

        and creationTime >= #{start}


        and creationTime <= #{end}


) AS off
FROM
sale_order a


        a.creationTime >= #{start}


        and a.creationTime <= #{end}

SELECT DATE_FORMAT( creationTime, '%h' ) as time, COUNT( productId ) as count FROM sale_order
payState = 1 AND orderState = 1
and creationTime >= #{start}
and creationTime <= #{end}
GROUP BY DATE_FORMAT( creationTime, '%h' ) ORDER BY DATE_FORMAT( creationTime, '%h' )

SELECT DATE_FORMAT( creationTime, '%d' ) as time, COUNT( productId ) as count FROM sale_order
payState = 1 AND orderState = 1
and creationTime >= #{start}
and creationTime <= #{end}
GROUP BY DATE_FORMAT( creationTime, '%d' ) ORDER BY DATE_FORMAT( creationTime, '%d' )

select pointName as time,COUNT(productId) as count from sale_order
payState = 1 AND orderState = 1
and creationTime >= #{start}
and creationTime <= #{end}
GROUP BY pointName ORDER BY COUNT(productId) desc LIMIT 0,10

SELECT payType time, COUNT( productId ) as count FROM sale_order
payState = 1 AND orderState = 1
and creationTime >= #{start}
and creationTime <= #{end}
GROUP BY payType

SELECT productId, productName, COUNT( a.productId ) saleCount, SUM( a.price ) saleMoney FROM sale_order a WHERE a.payState = 1 AND a.orderState = 1
and a.creationTime >= #{start}
and a.creationTime <= #{end}
and a.productName like '%${productName}%'
GROUP BY a.productId, a.productName
